clear;hold on;axis "equal";tanti = 100;a = 9;b = 4;mobius = @(z) (z*a+b)./(z+1); %% la funzione è la composizione di una traslazione di 1+0i,%% un reciproco dei punti%% un'omotetia di -5+0i con centro in 0+0i,%% una traslazione di 9+0i.f1 = @(x) x .+ 1;f2 = @(x) 1./x;f3 = @(x) (b-a).*x;f4 = @(x) x .+ a;var_v = linspace(0,2*pi,tanti);var_v = exp(var_v*1i)+1;plot(var_v);var_v2 = mobius(var_v);plot(var_v2,"r*");%% la figura risultante da var_v2 è una circonferenzaf4(f3(f2(f1(var_v))))-mobius(var_v) < 10^(-10)